import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { Card } from "react-bootstrap";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames
    .map((name) => {
      return {
        name: name,
        items: menuItems.filter((item) => item.category == name),
      };
    })
    .reverse();

  const Plate = (props) => {
    const { name, description, price, imgURL } = props.plate;

    // return (
    //   <Card style={{ width: 500 }}>
    //     <Card.Img variant="top" src={imgURL} />

    //     <Card.Body>
    //       <Card.Title>{name}</Card.Title>
    //       <Card.Text>{description}</Card.Text>
    //       <h2>${price}</h2>
    //     </Card.Body>
    //   </Card>
    // );
  };

  const [selectedId, setSelectedId] = useState(null);

  return (
    <>
      {menuItems.map((item) => (
        <motion.div layoutId={item._id} onClick={() => setSelectedId(item._id)} >
          {/* <Card style={{ width: 500 }}>
            <Card.Img variant="top" src={item.imgURL} />

            <Card.Body>
              <Card.Title>{item.name}</Card.Title>
              <Card.Text>{item.description}</Card.Text>
              <h2>${item.price}</h2>
            </Card.Body>
          </Card> */}

          <motion.h5>{item.name}</motion.h5>
          <motion.h2>{item.price}</motion.h2>
          <motion.h5>{item.description}</motion.h5>
        </motion.div>
      ))}

      <AnimatePresence>
				<div className="modal fade" id="
				" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
						<div className="modal-dialog" role="document">
							<div className="modal-content">
								<div className="modal-header">
									<h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
									<button type="button" className="close" data-dismiss="modal" aria-label="Close">
										<span aria-hidden="true">×</span>
									</button>
								</div>
								<div className="modal-body">
									
				
								</div>
								<div className="modal-footer">
									<button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
									<button type="button" className="btn btn-primary">Save changes</button>
								</div>
							</div>
						</div>
					</div>
					
        {selectedId && (
          <motion.div layoutId={selectedId}>
            <motion.h5>{"item.name"}</motion.h5>
            <motion.h2>{"item.price"}</motion.h2>
            <motion.button onClick={() => setSelectedId(null)} />
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );

  // return categories.map((cat) => (
  //   <>
  //     <h1>{cat.name}</h1>
  //     {cat.items.map((item) => (
  //       <Plate plate={item} />
  //     ))}
  //   </>
  // ));
};
