import {
  Box,
  Text,
  Badge,
  Heading,
  CloseButton,
  Button,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useEffect, useState } from "react";
import { CoursesGroup } from "./courseGroup";

export const ShoppingCart = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleOpened = () => setIsOpen((state) => !state);

  const total = () => {
    let total = 0;
    props.shoppingCart
      .map((course) => course.amount * course.plate.price)
      .forEach((x) => (total += x));
    return total;
  };

  useEffect(() => {
    setIsOpen(true);
  }, [props.shoppingCart]);

  return (
    <>
      <Box>
        <Box
          as={motion.div}
          position="fixed"
          bottom="0"
          right="0"
          boxShadow="lg"
          padding="3"
          bg="white"
          animate={{ opacity: 1 }}
          initial={{ opacity: 0 }}
          width="500px"
          borderRadius="lg"
          maxH="100%"
          maxW="100%"
          overflowY="scroll"
          bg="rgba(0,0,0,0.15)"
          css={{ backdropFilter: "blur(5px)" }}
        >
          <Heading as="h3" onClick={toggleOpened}>
            Shopping Cart {isOpen ? "V" : "^"}
          </Heading>
          {isOpen && (
            <motion.div animate={{ y: 0 }} initial={{ y: -100 }}>
              {props.shoppingCart.map((course) => (
                <Box rounded="l" padding={2} shadow="md">
                  <CloseButton
                    onClick={() => props.remove(course)}
                    display="inline"
                  />
                  <Text fontSize={20}>{course.plate.name}</Text>

                  <CoursesGroup
                    plate={course.plate}
                    amount={course.amount}
                    courseId={course.courseId}
                    display="inline"
                  />

                  <Stat>
                    <StatNumber>
                      ${course.amount * course.plate.price}
                    </StatNumber>
                    <StatHelpText>
                      {course.amount} {course.plate.name} a $
                      {course.plate.price} c/u
                    </StatHelpText>
                  </Stat>
                </Box>
              ))}

              <Stat>
                <StatLabel>Total</StatLabel>
                <StatNumber fontSize={40}>
                  ${props.shoppingCart != [] ? total() : "0"}
                </StatNumber>
              </Stat>

              <Button>Ir a Pagar</Button>
            </motion.div>
          )}
        </Box>
      </Box>
    </>
  );
};
