import {
  Badge,
  Box,
  Button,
  Heading,
  HStack,
  Image,
  Input,
  NumberDecrementStepper,
  NumberIncrementStepper,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  SimpleGrid,
  Text,
  Wrap,
  WrapItem,
} from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { CloseButton } from "react-bootstrap";
import { PlateIcon } from "./plateIcon";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [shoppingCart, setShoppingCart] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const [selectedItem, setSelectedItem] = useState(null);

  const addToShoppingCart = () => {
    setShoppingCart(shoppingCart.concat({...selectedItem, amount: 1, extra: null}))
    setSelectedItem(null)
  }
  return (
    <>
      <SimpleGrid columns={[1, 2, 3]} spacing="40px">
        {menuItems.map((item) => (
          <PlateIcon />

    
        ))}
      </SimpleGrid>
      <AnimatePresence>
        {selectedItem && (
          <>
            <Box
              as={motion.div}
              position="fixed"
              bg="rgba(255,255,255,0.5)"
              w="100%"
              h="100%"
              top="0"
              left="0"
              onClick={() => setSelectedItem(null)}
              transition={{ duration: 2 }}
            />
            <Box
              as={motion.div}
              // layoutId={selectedItem.token}
              position="fixed"
              top={0}
              boxShadow="dark-lg"
              rounded="xl"
              padding="3"
              bg="white"
              animate={{ opacity: 1 }}
              initial={{ opacity: 0 }}
            >
              <Box>
                <CloseButton onClick={() => setSelectedItem(null)} />
                <Wrap spacing={10} margin="2">
                  <Box>
                    <WrapItem as={motion.div} layoutId={selectedItem._id}>
                      <Image
                        as={motion.img}
                        src={selectedItem.imgURL}
                        rounded="xl"
                      />
                    </WrapItem>
                  </Box>
                  <Box margin="4">
                    <Box as={motion.div} layoutId={selectedItem.name}>
                      <Heading size="lg">{selectedItem.name}</Heading>
                    </Box>
                    <Text marginTop={10}>{selectedItem.description}</Text>
                    <Box as={motion.div}>
                      <Heading size="md">${selectedItem.price}</Heading>
                    </Box>
                    <HStack marginTop={20}>
                      <NumberInput size="lg" maxW={32} defaultValue={1} min={1}>
                        <NumberInputField />
                        <NumberInputStepper>
                          <NumberIncrementStepper />
                          <NumberDecrementStepper />
                        </NumberInputStepper>
                      </NumberInput>
                      <Input placeholder="Pedido especial?" size="lg" />
                      <Button
                        colorScheme="blue"
                        size="lg"
                        onClick={addToShoppingCart}
                      >
                        Pedir
                      </Button>
                    </HStack>
                  </Box>
                </Wrap>
              </Box>
            </Box>
          </>
        )}
      </AnimatePresence>
      {(shoppingCart != []) && (
        <Box
          as={motion.div}
          position="fixed"
          bottom="0"
          right="0"
          boxShadow="lg"
          padding="3"
          bg="white"
          animate={{ opacity: 1 }}
          initial={{ opacity: 0 }}
        >
          <Heading>shopping cart</Heading>

          {shoppingCart.map((item) => (
            <HStack>
              <Box as={motion.div} >
              <Image as={motion.img} src={item.imgURL} w="70" h="70" />

              </Box>
              <Box as={motion.div} >{item.amount} X {item.name}</Box>
            </HStack>
          ))}
        </Box>
      )}
    </>
  );
};
