import {
  Box,
  Button,
  CloseButton,
  Heading,
  HStack,
  NumberDecrementStepper,
  NumberIncrementStepper,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  SimpleGrid,
  Text,
  Input,
  WrapItem,
  Image,
  IconButton,
  GridItem,
  Modal,
  ModalBody,
  ModalOverlay,
  ModalContent,
} from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import { useState } from "react";
import { CoursesGroup } from "./courseGroup";

export const PlateWindow = (props) => {
  const { _id, imgURL, name, description, price } = props.item;
  const [courseId, setCourseId] = useState(Math.random());

  const [amount, setAmount] = useState(1);
  const setNewCourseId = () => {
    setCourseId(Math.random());
    setAmount(1);
  };

  const increment = () => setAmount((amount) => amount + 1);

  const decrement = () =>
    setAmount((amount) => {
      if (amount > 1) return amount - 1;
      else return amount;
    });

  return (
    <>
      {/* <Box
        as={motion.div}
        position="fixed"
        bg="rgba(0,0,0,0.15)"
        css={{ backdropFilter: "blur(5px)" }}
        w="100%"
        h="100%"
        top="0"
        left="0"
        onClick={props.close}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      /> */}

      <Modal
        // as={motion.div}
        // position="fixed"
        // w="90vw"
        // rounded="xl"
        // padding="3"
        // bg="white"
        // top="7vh"
        // layoutId={"box" + _id}
        // key={_id}
        // boxShadow="lg"
        // overflowY="scroll" overflowX="hidden"
      >
        <ModalOverlay />
        <ModalContent>

        <ModalBody>

          <CloseButton onClick={props.close} />
          <SimpleGrid gap={10} columns={[1,2]}>
            <Box colSpan="1">
              <Image
                as={motion.img}
                src={imgURL}
                style={{ borderRadius: 10, width: "100%" }}
                layoutId={"img" + _id}
              />
            </Box>
            <Box colSpan="2" >
              <Box margin="4">
                <Box as={motion.div} layoutId={"name" + _id}>
                  <Heading size="lg">{name}</Heading>
                </Box>
                <Text marginTop={10}>{description}</Text>
                <Heading as={motion.h2} layoutId={"price" + _id} size="md">
                  ${price}
                </Heading>
                <HStack>
                  <Button onClick={decrement}> - </Button>
                  <Heading>{amount}</Heading>
                  <Button onClick={increment}>+ </Button>

                  <CoursesGroup
                    plate={props.item}
                    amount={amount}
                    courseId={courseId}
                  />
                </HStack>

                <Input placeholder="Pedido especial?" size="lg" />
                <Button
                  colorScheme="blue"
                  size="lg"
                  onClick={() => {
                    props.addToShoppingCart(props.item, amount, courseId);
                    setNewCourseId();
                  }}
                >
                  Pedir
                </Button>
              </Box>
            </Box>
          </SimpleGrid>
          </ModalBody>
          </ModalContent>


      </Modal>
    </>
  );
};
