import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { Card, Modal } from "react-bootstrap";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames
    .map((name) => {
      return {
        name: name,
        items: menuItems.filter((item) => item.category == name),
      };
    })
    .reverse();

  const Plate = (props) => {
    const { name, description, price, imgURL } = props.plate;

    // return (
    //   <Card style={{ width: 500 }}>
    //     <Card.Img variant="top" src={imgURL} />

    //     <Card.Body>
    //       <Card.Title>{name}</Card.Title>
    //       <Card.Text>{description}</Card.Text>
    //       <h2>${price}</h2>
    //     </Card.Body>
    //   </Card>
    // );
  };

  const [selectedId, setSelectedId] = useState(null);

  return (
    <>
      {menuItems.map((item) => (
        <motion.div
          layoutId={item._id}
          onClick={() => setSelectedId(item)}
          style={{
						width: 500,
						margin: 20,
						backgroundColor: "gray",
						borderRadius: 20,
						top: "50%",
						left: "50%",
						zIndex: 1,
						padding: 20
          }}
        >

          <motion.h5>{item.name}</motion.h5>
          <motion.h2>{item.price}</motion.h2>
          <motion.h5>{item.description}</motion.h5>
        </motion.div>
      ))}

      <AnimatePresence>
        {selectedId && (
					<div style={{width: "100%", height: "100%", backgroundColor:"black"}}>
          <motion.div
            layoutId={selectedId._id}
            style={{
							display: 'block',
							position: 'fixed',
              width: 500,
              margin: 20,
              backgroundColor: "gray",
              borderRadius: 20,
							top: "50%",
							left: "50%",
							zIndex: 1,
							padding: 20
            }}
          >
            <motion.button onClick={() => setSelectedId(null)} >Close</motion.button>
            <motion.h5>{selectedId.name}</motion.h5>
            <motion.h2>{selectedId.price}</motion.h2>
          </motion.div>
					</div>
        )}
      </AnimatePresence>
    </>
  );

  // return categories.map((cat) => (
  //   <>
  //     <h1>{cat.name}</h1>
  //     {cat.items.map((item) => (
  //       <Plate plate={item} />
  //     ))}
  //   </>
  // ));
};
