import { Box, Text, Badge, Heading, CloseButton, Button } from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useEffect, useState } from "react";
import { CoursesGroup } from "./courseGroup";

export const ShoppingCart = (props) => {
  const [isOpen, setIsOpen] = useState(true);

  const toggleOpened = () => setIsOpen((state) => !state);

  const total = () => {
    let total = 0
    props.shoppingCart.map(course => course.amount * course.plate.price).forEach(x => total+=x);
    return total
  };

  useEffect(() => {setIsOpen(true)}, [props.shoppingCart])

  return (
    <>
      <Box >
        <Box
          as={motion.div}
          position="fixed"
          bottom="0"
          right="0"
          boxShadow="lg"
          padding="3"
          bg="white"
          animate={{ opacity: 1 }}
          initial={{ opacity: 0 }}
          width="500px"
          borderRadius="lg"
          maxH="100%"
          overflowY="scroll"
        >
          <Text fontSize="30" onClick={toggleOpened}>Shopping Cart</Text>
          {isOpen && (
            <motion.div animate={{ y: 0 }} initial={{ y: -100 }}>
              {props.shoppingCart.map((course) => (
                <Box rounded="l" padding={2}>
                  <CloseButton onClick={() => props.remove(course)} />
                  <Text fontSize={20}>
                    {" "}
                    {course.amount} X {course.plate.name}{" "}
                    <Badge> ${course.plate.price}</Badge>{" "}
                  </Text>
                  <CoursesGroup
                    plate={course.plate}
                    amount={course.amount}
                    courseId={course.courseId}
                  />

                  <Heading size="md">
                    ${course.amount * course.plate.price}
                  </Heading>
                </Box>
              ))}
              <Heading size="lg">
                Total $
                
                {props.shoppingCart!=[]
                  ? total()
                  : "0"}
              </Heading>
            </motion.div>
          )}
        </Box>
      </Box>
    </>
  );
};
