import {
  Box,
  Heading,
  HStack,
  Image,
  SimpleGrid,
} from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { PlateIcon } from "./plateIcon";
import { PlateWindow } from "./plateWindow";
import { ShoppingCart } from "./shoppingCart";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [shoppingCart, setShoppingCart] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const [selectedItem, setSelectedItem] = useState(null);

  const addToShoppingCart = (amount) => {
    setShoppingCart(
      shoppingCart.concat({plate: selectedItem, amount, extra: null })
    );
    setSelectedItem(null);
  };
  return (
    <>
      <SimpleGrid columns={[1, 2, 3]} spacing="40px">
        {menuItems.map((item) => (
          <PlateIcon item={item} setSelectedItem={setSelectedItem} />
        ))}
      </SimpleGrid>
      <AnimatePresence>
        {selectedItem && (
          <PlateWindow setSelectedItem={setSelectedItem} item={selectedItem} addToShoppingCart={addToShoppingCart} />
        )}
      </AnimatePresence>
      {shoppingCart != [] && <ShoppingCart shoppingCart={shoppingCart} />}
    </>
  );
};
