import { CheckIcon } from "@chakra-ui/icons";
import {
  Alert,
  AlertDescription,
  AlertIcon,
  AlertTitle,
  Badge,
  Box,
  Button,
  CircularProgress,
  CircularProgressLabel,
  CloseButton,
  Heading,
  HStack,
  IconButton,
  Progress,
  ProgressLabel,
  SimpleGrid,
  Stack,
  Text,
  VStack,
  Wrap,
} from "@chakra-ui/react";
import { useEffect } from "react";
import { useState } from "react/cjs/react.development";
import { server } from "../../../config";
import { changeOrderStatus } from "../../../scripts/changeOrderStatus";

const Course = ({ course }) => {
  const {
    amount,
    special,
    courseMoment,
    aditionalOptionsConfiguration,
    variant,
    menuItem,
    extras,
    price,
  } = course;
  return (
    <Box>
      <Text>
        {amount} x {menuItem.name} : {variant.name}{" "}
      </Text>
      <Box ml={10}>
        <Text>{special}</Text>
        <Text>
          {aditionalOptionsConfiguration.map((option) => (
            <Text>
              {" "}
              {option.propertyName}: {option.value}{" "}
            </Text>
          ))}
        </Text>

        <Text>
          {extras.map((extra) => (
            <Text>+{extra.name}</Text>
          ))}
        </Text>
      </Box>
    </Box>
  );
};

export const Order = ({ order }) => {
  const [courses, setCourses] = useState([]);
  const [end, setEnd] = useState(0);
  const { _id, requestTimeDate, deliveryLocation, status, orderNote } = order;

  let date = new Date(null);
  date.setMilliseconds(end - Date.parse(requestTimeDate)); // specify value for SECONDS here
  var delayClock = date.toISOString().substring(14, 19);

  const delayTreshold = 300;
  const delaySeconds = (end - Date.parse(requestTimeDate)) / 1000;
  const percentageToDelayed = (delaySeconds / delayTreshold) * 100;
  const isDelayed = delaySeconds > delayTreshold;

  const acceptOrder = () => {
    changeOrderStatus({ id: _id, newStatus: "preparing" }).then((res) => {
      console.log(JSON.stringify(res));
    });
  };

  useEffect(() => {
    setInterval(() => {
      setEnd(Date.now());
    }, 1000);
  }, []);

  useEffect(() => {
    fetch(server + "/api/order/courses/" + _id)
      .then((res) => res.json())
      .then((res) => setCourses(res));
  }, []);

  return (
    <Box
      p={5}
      m={3}
      shadow="lg"
      rounded="lg"
      bg={isDelayed ? "red.100" : "blue.100"}
    >
      <Box
        mt="-10"
        bg={isDelayed ? "red.100" : "blue.100"}
        w="fit-content"
        h="fit-content"
        rounded={"full"}
      >
        <CircularProgress
          value={percentageToDelayed}
          color={isDelayed ? "red.400" : "blue.400"}
          size="60px"
        >
          <CircularProgressLabel fontWeight={"bold"}>
            {delayClock}
          </CircularProgressLabel>
        </CircularProgress>
      </Box>

      <SimpleGrid columns={2} spacing="1" m="2">
        <Stack>
          <Text>{status=="idle" ? "En Espera..": ""} </Text>
          <Progress
            colorScheme={isDelayed ? "red" : "blue"}
            size="xs"
            isIndeterminate
          />
        </Stack>
        <Stack>
        <Text>{status=="preparing" ?  "Preparando" : ""} </Text>
          <Progress
            colorScheme={isDelayed ? "red" : "blue"}
            size="xs"
            value={0}
          />
        </Stack>
      </SimpleGrid>

      <HStack spacing="5">
        <Alert status={isDelayed ? "error" : "info"} size="lg">
          <AlertIcon />
          <AlertTitle mr={2}>
            Mesa {deliveryLocation} -{" "}
            {isDelayed ? "Pedido Atrasado!" : "Nuevo Pedido"}
          </AlertTitle>

          <AlertDescription></AlertDescription>
        </Alert>
        <IconButton
          icon={<CloseButton />}
          colorScheme="red"
          variant={"ghost"}
        />
        <IconButton
          icon={<CheckIcon />}
          onClick={acceptOrder}
          colorScheme="green"
          variant={"ghost"}
        />
      </HStack>
      {status}
      {orderNote != "unknown extras" && <Text>{orderNote}</Text>}

      {courses.map((course) => (
        <Course course={course} />
      ))}
    </Box>
  );
};
