import { CheckIcon } from "@chakra-ui/icons";
import {
  Alert,
  AlertDescription,
  AlertIcon,
  AlertTitle,
  Box,
  Button,
  CircularProgress,
  CircularProgressLabel,
  CloseButton,
  Heading,
  HStack,
  IconButton,
  Progress,
  Text,
  Wrap,
} from "@chakra-ui/react";
import { useEffect } from "react";
import { useState } from "react/cjs/react.development";
import { server } from "../../../config";

const Course = ({ course }) => {
  const {
    amount,
    special,
    courseMoment,
    aditionalOptionsConfiguration,
    variant,
    menuItem,
    extras,
    price,
  } = course;
  return (
    <Box>
      <Text>
        {amount} x {menuItem.name} : {variant.name}{" "}
      </Text>
      <Box ml={10}>
        <Text>{special}</Text>
        <Text>
          {aditionalOptionsConfiguration.map((option) => (
            <Text>
              {" "}
              {option.propertyName}: {option.value}{" "}
            </Text>
          ))}
        </Text>

        <Text>
          {extras.map((extra) => (
            <Text>+{extra.name}</Text>
          ))}
        </Text>
      </Box>
    </Box>
  );
};

export const Order = ({ order }) => {
  const [courses, setCourses] = useState([]);
  const [end, setEnd] = useState(0);
  const { _id, timeDate, deliveryLocation, status, orderNote } = order;

  let date = new Date(null);
  date.setMilliseconds(end - Date.parse(timeDate)); // specify value for SECONDS here
  var delayClock = date.toISOString().substring(14, 19);

  const delayTreshold = 300;
  const delaySeconds = (end - Date.parse(timeDate)) / 1000;
  const percentageToDelayed = (delaySeconds / delayTreshold) * 100;
  const isDelayed = delaySeconds > delayTreshold;

  useEffect(() => {
    setInterval(() => {
      setEnd(Date.now());
    }, 1000);
  }, []);

  useEffect(() => {
    fetch(server + "/api/order/courses/" + _id)
      .then((res) => res.json())
      .then((res) => setCourses(res));
  }, []);

  return (
    <Box
      p={5}
      m={3}
      shadow="lg"
      rounded="lg"
      bg={isDelayed ? "red.100" : "green.100"}
    >
      <HStack spacing="5">
        <IconButton
          icon={<CloseButton />}
          colorScheme="red"
          variant={"ghost"}
        />
        <IconButton
          icon={<CheckIcon />}
          colorScheme="green"
          variant={"ghost"}
        />
      </HStack>
      <Box mt="-10" bg="white" w="fit-content" h="fit-content" rounded={"full"}>
        <CircularProgress
          value={percentageToDelayed}
          color="red.400"
          size="60px"
        >
          <CircularProgressLabel fontWeight={"bold"}>
            {delayClock}
          </CircularProgressLabel>
        </CircularProgress>
      </Box>

      <Alert status={isDelayed ? "error" : "info"} size="lg">
        <AlertIcon />
        <AlertTitle mr={2}>
          Mesa {deliveryLocation} -{" "}
          {isDelayed ? "Pedido Atrasado!" : "Nuevo Pedido"}
        </AlertTitle>

        <AlertDescription></AlertDescription>
      </Alert>

      {orderNote != "unknown extras" && <Text>{orderNote}</Text>}

      {courses.map((course) => (
        <Course course={course} />
      ))}
    </Box>
  );
};
