import { AddIcon, AttachmentIcon, ChevronDownIcon, PhoneIcon, PlusSquareIcon } from "@chakra-ui/icons";
import {
	Box,
	Button,
	CloseButton,
	Heading,
	HStack,
	Text,
	Input,
	Image,
	Modal,
	ModalBody,
	ModalOverlay,
	ModalContent,
	VStack,
	Badge,
	RadioGroup,
	Stack,
	Radio,
	Checkbox,
	Textarea,
	useDisclosure,
	ModalHeader,
	ModalCloseButton,
	ModalFooter,
	InputGroup,
	InputLeftElement,
	InputRightElement,
	Tag,
	Avatar,
	TagLabel,
	IconButton,
	Select,
	Popover,
	PopoverTrigger,
	Portal,
	PopoverContent,
	SimpleGrid,
	PopoverArrow,
	PopoverHeader,
	PopoverCloseButton,
	PopoverBody,
	Menu,
	MenuButton,
	MenuList,
	MenuItem,
	Table
} from "@chakra-ui/react";





import { useState } from "react";
import { newMenuItem } from "../../scripts/newMenuItem";
import { dietTagData, DietTags, getDietData } from "../menu/dietTags";

export const NewMenuItem = () => {
	const { isOpen, onOpen, onClose } = useDisclosure()
	const [menuItem, setMenuItem] = useState("")
	const [tags, setTags] = useState(["vegan"])
	const [variants, setVariants] = useState([])

	const addVariant = (variant) => setVariants(variants.concat(variant))

	const handleSubmit = async () => {
		const itemObj = JSON.parse(menuItem)
		console.log(itemObj)
		const response = await newMenuItem(itemObj)
	}

	const handleChange = (event) => {
		setMenuItem(event.target.value)
	}


	const NewVariant = () => {

		const [variant, setVariant] = useState({
			name: "", price: 0
		})

		const handleChange = (event) => {

			const { name, value } = event.target

			setVariant({...variant, [name]: value})

		}

		const createVariant = () => {
			addVariant(variant)
		}

		return (
			<Stack spacing={4} border={"1px solid gray"} p="3">

				<Heading size={"md"}> Variantes </Heading>

				{variants.map(variant => <Heading>{variant.name} @ {variant.price}</Heading>)}

				<InputGroup>

					<Input type='text' placeholder='Nombre de la variante' name="name" value={variant.name} onChange={handleChange} />
				</InputGroup>

				<InputGroup>
					<InputLeftElement
						pointerEvents='none'
						color='gray.300'
						fontSize='1.2em'
						children='$'
					/>
					<Input placeholder='Precio' type={"number"} name="price" value={variant.price} onChange={handleChange} />
				</InputGroup>

				<Button rightIcon={<AddIcon />} colorScheme='teal' variant='outline' onClick={createVariant}>
					Agregar Variante
				</Button>

			</Stack>
		)
	}

	const DietTagsEdit = (props) => {

		const [onCustomInput, setIsOnCustomInput] = useState(false)
		const [customTag, setCustomTag] = useState("")

		const handleCustomTagInput = (event) => setCustomTag(event.target.value)

		const handleAddCustomTag = () => {
			addTag(customTag)
			setCustomTag("")

		}

		const removeTag = tagName => {
			setTags(tags.filter(tag => tag != tagName))
		}

		const addTag = tagName => setTags(tags.concat(tagName))


		return (
			<>


				<Box p="3" rounded="md">
					<Heading size="md"> Etiquetas  </Heading>
					{props.tags.map((tag) => {
						const { imgURL, name } = getDietData(tag);

						const remove = () => { removeTag(tag) }

						return (
							<Tag size="lg" borderRadius="full" m="1">
								<Avatar src={imgURL} size="xs" name={name} ml={-1} mr={2} />
								<TagLabel>{name}</TagLabel>
								<CloseButton onClick={remove} />
							</Tag>
						);
					})}



					{onCustomInput ? <HStack spacing="2" ><Input placeholder="Otra etiqueta" onChange={handleCustomTagInput} value={customTag} /> <Button onClick={handleAddCustomTag}>Agregar</Button></HStack >
						:
						<Menu>
							<MenuButton as={Button} rightIcon={<ChevronDownIcon />}>
								Añadir
							</MenuButton>
							<MenuList>
								{dietTagData.map(diet => {
									const add = () => { addTag(diet.name) }
									if (!tags.includes(diet.name))
										return (
											<MenuItem onClick={add}>{diet.displayName}</MenuItem>)


								})}


								<MenuItem ><Button onClick={() => setIsOnCustomInput(true)}>Otra...</Button></MenuItem>


							</MenuList>
						</Menu>
					}


				</Box>
			</>

		)
	};

	return (
		<>
			<Button
				size='md'
				height='48px'
				width='200px'
				border='2px'
				borderColor='green.500' onClick={onOpen}>Añadir Nuevo Plato</Button>



			<Modal isOpen={isOpen} onClose={onClose}>
				<ModalOverlay />
				<ModalContent>
					<ModalHeader>Nuevo Plato</ModalHeader>
					<ModalCloseButton />
					<ModalBody>


						<VStack gap={10} >
							<Button style={{ borderRadius: 10, width: "100%", height: 100 }} rightIcon={<AttachmentIcon />
							} >
								Elegir una imagen
							</Button>
							<Stack spacing="5" w="100%" p="5">
								<Input placeholder='Nombre del Item del Menú' size='lg' />



								<Textarea placeholder="Descripción" />

								<NewVariant />


								<DietTagsEdit tags={tags} />
								<Button> Añadir otra etiqueta</Button>





							</Stack>

						</VStack>

					</ModalBody>

					<ModalFooter>
						<Button mr={3} onClick={onClose}>
							Close
						</Button>
						<Button colorScheme='blue' onClick={handleSubmit} >
							Enviar
						</Button>
					</ModalFooter>
				</ModalContent>
			</Modal>
		</>
	)

}
