import {
  Box,
  Image,
  Stat,
  StatNumber,
} from "@chakra-ui/react";
import { motion } from "framer-motion";

export const PlateIcon = (props) => {
  const { token, _id, imgURL, name, price } = props.item;

  return (
    <Box
      as={motion.div}
      layoutId={"box" + _id}
      key={_id}
      style={{
        height: "400px",
        backgroundSize: "100%",
      }}
      
      animate={{ scale: 1 }}
      initial={{ scale: 0.5 }}
      onClick={props.open}
    >
      <Box as={motion.div} style={{ height: "70%" }}>
        <Image
          as={motion.img}
          src={imgURL}
          variant="top"
          rounded="lg"
          layoutId={"img" + _id}

          style={{
            objectFit: "cover",
            height: "100%",
            width: "100%",
            borderRadius: 15,
          }}
        />
      </Box>

      <Box
        as={motion.div}
        // layoutId={"name" + _id}
        mt="1"
        fontWeight="semibold"
        as="h3"
        lineHeight="tight"
        isTruncated
      >
        {name}
      </Box>

      <Stat>
        <StatNumber>${price}</StatNumber>
      </Stat>
    </Box>
  );
};
