import { Button, SimpleGrid } from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { Badge, Card, Col, Container, Modal, Row } from "react-bootstrap";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames
    .map((name) => {
      return {
        name: name,
        items: menuItems.filter((item) => item.category == name),
      };
    })
    .reverse();

  const [selectedItem, setSelectedItem] = useState(null);

  return (
    <>
      <SimpleGrid columns={[2, null, 3]} spacing='4px'>

        {menuItems.map((item) => (
          <motion.div
            style={{
              height: "400px",
              backgroundSize: "100%",
            }}
            onClick={() => setSelectedItem(item)}
            className="col-4"
            rounded
            layoutId={item.token}
          >
            <motion.div layoutId={item._id} style={{ height: "70%" }}>
              <Card.Img
                src={item.imgURL}
                variant="top"
                className="rounded-lg "
                style={{
                  objectFit: "cover",
                  height: "100%",
                  borderRadius: 15,
                }}
              />
            </motion.div>
            <motion.div layoutId={item.name}>
              <h4 style={{ textAlign: "center", margin: 10 }}>
                {item.name}{" "}
                <Badge pill as={motion.span} layoutId={item.price}>
                  {" "}
                  ${item.price}
                </Badge>
              </h4>
            </motion.div>
          </motion.div>
        ))}
      </SimpleGrid>
      <AnimatePresence>
        {selectedItem && (
          <>
            <motion.div
              style={{
                width: "100%",
                height: "100%",
                backgroundColor: "white",
                position: "fixed",
                top: 0,
              }}
              initial={{ opacity: 0 }}
              animate={{ opacity: 0.5 }}
              onClick={() => setSelectedItem(null)}
            />
            <motion.div
              style={{
                position: "fixed",
                top: "0%",
                left: "0%",
                marginLeft: "20%",
                marginTop: "10%",
                padding: 20,
                background: "white",
                width: "60%",
                maxHeight: "60%",
                borderRadius: 20,
              }}
              onClick={() => setSelectedItem(selectedItem)}
              className="shadow-lg "
              layoutId={selectedItem.token}
            >
              <Row>
                <Col>
                  <motion.div layoutId={selectedItem._id}>
                    <img
                      as={motion.img}
                      variant="top"
                      src={selectedItem.imgURL}
                      style={{ borderRadius: 20 }}
                    />
                  </motion.div>
                </Col>
                <Col className="m-3">
                  <motion.div layoutId={selectedItem.name}>
                    <h2>{selectedItem.name}</h2>
                  </motion.div>
                  <hr />
                  <p>{selectedItem.description}</p>
                  <h2>
                    <motion.div layoutId={selectedItem.price}>
                      <Badge pill>${selectedItem.price}</Badge>
                    </motion.div>
                  </h2>
                  <Button colorScheme="blue">Button</Button>
                </Col>
              </Row>
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </>
  );
};
