import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { Button, Card, Container, Modal } from "react-bootstrap";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames
    .map((name) => {
      return {
        name: name,
        items: menuItems.filter((item) => item.category == name),
      };
    })
    .reverse();

  const Plate = (props) => {
    const { name, description, price, imgURL } = props.plate;

    // return (
    //   <Card style={{ width: 500 }}>
    //     <Card.Img variant="top" src={imgURL} />

    //     <Card.Body>
    //       <Card.Title>{name}</Card.Title>
    //       <Card.Text>{description}</Card.Text>
    //       <h2>${price}</h2>
    //     </Card.Body>
    //   </Card>
    // );
  };

  const [selectedItem, setSelectedItem] = useState(null);

  return (
    <>
      <Container style={{width: "100%"}} >
        {menuItems.map((item) => (
          <>
            <Card
              as={motion.div}
              style={{ width: "22rem", height:"22rem", float: "left", borderRadius: 10, margin:20 }}
              layoutId={item._id}
              onClick={() => setSelectedItem(item)}
							className="bg-dark text-white"
            >
              <Card.Img as={motion.img} variant="top" src={item.imgURL}  />
              <Card.ImgOverlay as={motion.div}>
                <Card.Title as={motion.h2}>{item.name}</Card.Title>
                <Card.Text as={motion.p}>{item.description}</Card.Text>
                <Button as={motion.button} variant="primary">
                  ${item.price}
                </Button>
              </Card.ImgOverlay>
            </Card>
          </>
        ))}
      </Container>
      <AnimatePresence>
        {selectedItem && (
					<>
					<motion.div style={{width:"100%", height:"100%", backgroundColor:"black", position: "fixed", top:0,opacity: 0.5}} onClick={() => setSelectedItem(null)}/>
          <Card
            as={motion.div}
            style={{position: "fixed", top:"0%", left:"0%", margin:20 }}
            layoutId={selectedItem._id}
            onClick={() => setSelectedItem(selectedItem)}
          >
            <Card.Img as={motion.img} variant="top" src={selectedItem.imgURL} />
            <Card.Body as={motion.div}>
              <Card.Title as={motion.h2}>{selectedItem.name}</Card.Title>
              <Card.Text as={motion.p}>{selectedItem.description}</Card.Text>
              <Button as={motion.button} variant="primary">
                ${selectedItem.price}
              </Button>
            </Card.Body>
          </Card>
					</>

        )}
      </AnimatePresence>
    </>
  );

  // return categories.map((cat) => (
  //   <>
  //     <h1>{cat.name}</h1>
  //     {cat.items.map((item) => (
  //       <Plate plate={item} />
  //     ))}
  //   </>
  // ));
};
