import {
  Box,
  Button,
  CloseButton,
  Heading,
  HStack,
  NumberDecrementStepper,
  NumberIncrementStepper,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  Wrap as Grid,
  Text,
  Input,
  WrapItem,
  Image,
  IconButton,
  GridItem,
  StatNumber,
  Stat,
} from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import { useState } from "react";
import { CoursesGroup } from "./courseGroup";

export const PlateWindow = (props) => {
  const { _id, imgURL, name, description, price } = props.item;
  const [courseId, setCourseId] = useState(Math.random());

  const [amount, setAmount] = useState(1);
  const setNewCourseId = () => {
    setCourseId(Math.random());
    setAmount(1);
  };

  const increment = () => setAmount((amount) => amount + 1);

  const decrement = () =>
    setAmount((amount) => {
      if (amount > 1) return amount - 1;
      else return amount;
    });

  return (
    <>
      <Box
        as={motion.div}
        position="fixed"
        bg="rgba(0,0,0,0.6)"
        w="100%"
        h="100%"
        top="0"
        left="0"
        onClick={() => props.setSelectedItem(null)}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      />

      <Box
        as={motion.div}
        position="fixed"
        boxShadow="dark-lg"
        rounded="xl"
        padding="3"
        bg="white"
        top="100"
        layoutId={"box" + _id}
        key={_id}
      >
        <Box as={motion.div} overflowY="scroll" overflowX="hidden">
          <CloseButton onClick={props.close} />
          <Grid gap={10}>
            <GridItem colSpan="1">
              <Image
                as={motion.img}
                src={imgURL}
                rounded="xl"
                layoutId={"img" + _id}
              />
            </GridItem>
            <GridItem colSpan="2">
              <Box margin="4">
                <Box as={motion.div} layoutId={"name" + _id}>
                  <Heading size="lg">{name}</Heading>
                </Box>
                <Text marginTop={10}>{description}</Text>
                <Stat>
                  <StatNumber as={motion.div} layoutId={"price" + _id}>
                    ${price}
                  </StatNumber>
                </Stat>
                <HStack>
                  <Button onClick={decrement}> - </Button>
                  <Heading>{amount}</Heading>
                  <Button onClick={increment}>+ </Button>

                  <CoursesGroup
                    plate={props.item}
                    amount={amount}
                    courseId={courseId}
                  />
                </HStack>

                <Input placeholder="Pedido especial?" size="lg" />
                <Button
                  colorScheme="blue"
                  size="lg"
                  onClick={() => {
                    props.addToShoppingCart(amount, courseId);
                    setNewCourseId();
                  }}
                >
                  Pedir
                </Button>
              </Box>
            </GridItem>
          </Grid>
        </Box>
      </Box>
    </>
  );
};
