import { Box, Image, Text } from "@chakra-ui/react";
import { motion } from "framer-motion";

const LastPlateIcon = (props) => (
  <PlateIcon plate={props.plate} marginLeft="0" />
);

const PlateIcon = (props) => (
  <Image
    as={motion.img}
    borderRadius="full"
    boxSize="40px"
    src={props.plate.imgURL}
    alt={props.plate.name}
    fit="cover"
    shadow="md"
    display="inline"
    marginLeft="-30"
  />
);

export const CoursesGroup = (props) => {
  const { amount, plate, courseId } = props;

  const Icons = () => {
    let icons = [];
    let i = 1;
    while (i <= amount - 1) {
      icons.push(<PlateIcon layoutId={`${courseId}-${i}`} />);
      i++;
    }

    <LastPlateIcon layoutId={`${courseId}-${i+1}`} />;

    return icons;
  };

  return (
    <Box
      borderWidth="1px"
      borderRadius="lg"
      margin="5"
      padding="2"
      display="block"
    >
      <Text display="inline" fontSize="lg">
        {amount} X{" "}
      </Text>
      <Box marginLeft="35" display="inline">
        <Icons />
        {Array(amount - 1).fill(
          <PlateIcon plate={plate} layoutId={`${plate.name}${Math.random()}`} />
        )}
        <LastPlateIcon plate={plate} />
      </Box>
    </Box>
  );
};
