import { useToast } from "@chakra-ui/react";


export const uploadImg = async (file) => {
  const formData = new FormData();

  formData.append("file", file);

  const options = {
    method: "POST",
    body: formData,
  };

  return fetch("http://localhost:5000/api/image/upload", options)
  .then(res => res.json())

};
