import { Box, Heading, Image, Text } from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";

const LastPlateIcon = (props) => (
  <PlateIcon
    as={motion.img}
    plate={props.plate}
    marginLeft="0"
    cid={props.cid}
    border="2px solid red"
    initial={{y: -100}}


  />
);

const PlateIcon = (props) => (
  <Image
    as={motion.img}
    layoutId={props.cid}
    borderRadius="lg"
    boxSize="40px"
    src={props.plate.imgURL}
    alt={props.plate.name}
    fit="cover"
    shadow="lg"
    shadowColor="white"
    display="inline"
    marginLeft="-15"
    animate={{ y: 0 }}


    border={props.border}
  />
);

const Icons = (props) => {
  const { amount, courseId, plate } = props;

  let icons = [];
  let i = 1;
  while (i <= amount - 1) {
    icons.push(<PlateIcon plate={plate} cid={courseId + i} />);
    i++;
  }

  icons.push(<LastPlateIcon plate={plate} cid={courseId + i} />);

  return icons;
};

export const CoursesGroup = (props) => {
  const { amount, plate, courseId, extra } = props;

  return (
    <AnimatePresence>
      <Box
        as={motion.div}
        margin="5"
        padding="2"
        display="block"
        layoutId={courseId}
      >
        <Box marginLeft="55" display="inline">
          <Icons amount={amount} courseId={courseId} plate={plate} />
        </Box>
      </Box>
    </AnimatePresence>
  );
};
